import axios from "axios";

const token =
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzMzQyYmRiMjY5NDkwODA4MDM2MmEyYiIsImlhdCI6MTY2NDUzMTEyNCwiZXhwIjoxNjY0NTY3MTI0fQ.0TYI2hibkL3y09lFMZ439LNchysxSGWSQAs7VOXqcMU";

  const addToken = token => {
    axios.defaults.headers.common.Authorization = `Bearer ${token}`;
}
  const instance = axios.create({
  baseURL: "http://localhost:4500/api/dails",
  headers: {
    Authorization: `Bearer ${token}`,
  },
});
const auth=axios.create({
  baseURL:"http://localhost:4500/api/user/"
})

export const allMoney = async () => {
  try {
    const data = instance.get(`/`);
    return  (await data).data.contacts;
  } catch (error) {
    console.log(error);
  }
};
export const deleteItemFetch = async (id) => {
  try {
    const data = instance.delete(`/${id}`);
    return  (await data).data.contacts;
  } catch (error) {
    console.log(error);
  }
};
export const changeItemFetch = async (id,name,date,description,importance,value) => {
  try {
    const data = instance.patch(`/${id}`);
    return  (await data).data.contacts;
  } catch (error) {
    console.log(error);
  }
};
export const Login = async ({Login,password}) => {
  try {
    const data = auth.post(`/login`,{email:Login,password:password});
    return  (await data);
  } catch (error) {
    console.log(error);
  }
};
